import { Component, DoCheck, OnInit } from '@angular/core';

//Interface
import { TaskList } from '../../model/task-list';
import { first, last } from 'rxjs';

@Component({
  selector: 'app-todo-list',
  templateUrl: './todo-list.component.html',
  styleUrls: ['./todo-list.component.scss']
})
export class TodoListComponent implements DoCheck{

  public taskList: Array<TaskList>= [

  ];
  public deleteItem(event: number) {
    this.taskList.splice(event, 1);
  }
  public deleteAll(){
    const confirm = window.confirm("Você realmente deseja Deletar tudo?");

    if(confirm){
      this.taskList = [];
    }

  }
  public setEmit(event: string){
    this.taskList.push({task: event, checked: false});
  }
  public validationInput(event: string, index: number){
    if(!event.length){
      const confirm = window.confirm("Task está vazia, deseja deletar?");
      if(confirm == true){
        this.deleteItem(index);
      }
    }
  }



  constructor(){}
  ngDoCheck(): void {
    this.taskList.sort((first, last) => Number(first.checked) - Number(last.checked));
  }



}
<section [ngClass]="{'loading': !taskList.length}">
    <ul *ngIf="taskList.length">
        <li *ngFor="let item of taskList; let i = index">
            <input type="checkbox" [(ngModel)]="item.checked">
            <input type="text" [(ngModel)]="item.task" [ngClass]="{'checked': item.checked}">

            <button (click)="deleteItem(i)">
                <i class="fa-solid fa-trash" ></i>
            </button>
        </li>
    </ul>
</section>

<app-todo-input-add-itens (emitItem)="setEmit($event)"></app-todo-input-add-itens>

<app-todo-button-delete-all *ngIf="taskList.length" (click)="deleteAll()"></app-todo-button-delete-all>
